$(document).ready(function(){var navTarget=$("body").attr("data-page-url");navTarget=navTarget.replace(/\/+$/,"");$(".active-link").removeClass("active-link");$('a[href="'+navTarget+'"]').addClass("active-link");$(document).on("click",".js-menu-toggle",function(){if($("body").hasClass("menu--open")){$("body").removeClass("menu--open")}else{$("body").addClass("menu--open")}});$(document).on("click",".menu__list__item__link",function(){if($(".menu").hasClass("menu--open")){$(".menu").removeClass("menu--open")}})});
